#coding:utf-8

"""
ID:          issue-3259
ISSUE:       3259
TITLE:       String right truncation error when char column longer than 4096 bytes compared with string constant
DESCRIPTION:
JIRA:        CORE-2875
FBTEST:      bugs.core_2875
"""

import pytest
from firebird.qa import *

init_script = """
	create table tab1 (id int, txt varchar(5000) character set none);
	commit;
	insert into tab1 values (1, rpad('', 5000, '1'));
	commit;
"""

db = db_factory(init=init_script)

test_script = """
	update tab1 set id = 2 where txt = _win1251 'x';
	commit;
	set list on;
	select id, txt from tab1;
"""

act = isql_act('db', test_script, substitutions=[('[ \t]+', ' ')])

expected_stdout = """
	ID                              1
	TXT                             
"""

@pytest.mark.version('>=3.0')
def test_1(act: Action):
    act.expected_stdout = expected_stdout
    act.execute()
    assert act.clean_stdout == act.clean_expected_stdout

